import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import React from "react";



const Book = ( {book}) => {
    return (
        <div className="book">
        <a href="">
            <figure className="book__img--wrapper">
                <img src={book.url} alt="" />
            </figure>
        </a>
        <div className="boook__title">
            <a href="" className='book__title--link'>{book.title}</a>
        </div>
        <div className="book__ratings">

        {new Array(book.rating).fill(book.rating).map( (element,index) =>  <FontAwesomeIcon icon="star"  key={index}/>)}

           
        </div>
        <div className="book__price">
            {/* check if book price exist if it does print og price and sale. No sale then use og  */}
        {
            book.salePrice ?  (<>
            <span className="book__price--normal">
            ${book.originalPrice.toFixed(2)}
        </span>
        ${book.salePrice.toFixed(2)} </> 
            ): (  <>${book.originalPrice.toFixed(2)}  </>)
        }

           
        </div>

    </div>
    )
}

export default Book
