import React, { useState } from "react";

import Book from "../components/ui/Book";

const   Bookss = ({books: initialBooks})=> {

const [books, setBooks] = useState(initialBooks);
    function filterBooks(filter){
        if (filter === "Low_To_High"){
            setBooks  (  books.slice().sort ( (a,b) => (a.salePrice || a.originalPrice) - (b.salePrice || b.originalPrice) ))
        }

        if (filter === "High_To_Low"){
            setBooks  (  books.slice().sort ( (a,b) => (b.salePrice || b.originalPrice) - (b.salePrice || b.originalPrice) ))
        }
    }   

    return (
        <div id="books__body">
            <main id="books__main">
                <section>
                    <div className="books__container">
                        <div className="row">
                            <div className="books__header">
                                <h2 className="section__title books__header--title" >All Books
                                </h2>

                                <select id="filter" defaultValue="DEFAULT" onChange={ (event) => filterBooks(event.target.value)}>
                                    <option value="DEFAULT" disabled>Sort</option>
                                    <option value="Low_To_High">Price, low to high</option>
                                    <option value="High_To_Low">Price, high to low</option>
                                    <option value="RATING">Rating</option>
                                </select>
                            </div>
                            <div className="books">
                                {
                                            books.map( book => <Book book= {book} key={book.id} />)
                                }
                            </div>

                        </div>
                    </div>
                </section>
            </main>
        </div>
    )
}

export default Bookss
