import Nav from "./components/nav";
import Landing from "./components/landingpage";
import Highlights from "./components/highlights";
import Features from "./components/ui/features";
import DiscountBook from "./components/ui/DiscountBook";
import Explore from "./components/Explore";
import Footer from "./components/Footer";
import { BrowserRouter as Router, Route, Link} from "react-router-dom";
import Home from "./pages/Home";
import Bookss from "./pages/Books";
import Book from "./components/ui/Book";
import { books } from "./data";
import BookInfo from "./pages/BookInfo";
import Cart from "./pages/Cart";
import React, { useEffect, useState } from "react";


function App() {
  const [cart,setCart] = useState([]);

  function addToCart(book){
    const dupItem = cart.find(item => +item.id === +book.id)
    if(dupItem){
      setCart( 
          cart.map( (item) => {
            if(item.id === dupItem.id){
              return { ...item, quantity: item.quantity+1,
              };
            }else{
              return item
            }
          })
      );
        }else{
          setCart
        }
    }


  useEffect ( ()=> {
    console.log(cart)
  },[])

  return (


    <Router>
    <div className="App">

      <Nav/>
      <Route  path="/"  exact component={Home}/>
      <Route path="/books" exact render={ () =>  <Bookss books= {books}/>}/>
      <Route path="/books/:id" exact render={ () =>  <BookInfo books= {books} addToCart={addToCart}/>} />
      <Route path="/cart" render={ () =>  <Cart books= {books}/>}/>
      <Footer/>
    </div>
    </Router>
  );
}

export default App;
